/**
 * 创建
 * @param letterLength
 * @returns {string}
 */
const createRandom = function (letterLength = 1) {
    const result = [];
    for (let i = 0; i < letterLength; i++) {
        const randomASCIINum = Math.ceil(Math.random() * 25); // 随机生成 0 - 25 的数字
        result.push(String.fromCharCode(65 + randomASCIINum)); // A - Z 的 ASCII码: [65 - 90]
    }
    return result.join('');
}
/**
 * 创建随机密码
 *
 */
const createPasssword = function () {
    const hashPassword = String(md5(String(new Date().getTime()))).substring(0,15); // 拼接时间戳 以免出现相同的
    let result = [];
    for (let i = 0; i < hashPassword.length; i++) {
        if (i % 2 === 0) {
            if (/^[0-9]$/.test(hashPassword[i])) {
                result.push(createRandom());
            }else{
                result.push(hashPassword[i])
            }
        } else if (/^[a-zA-Z]$/.test(hashPassword[i])) {
            // console.log(hashPassword[i],i)
            result.push(Math.ceil(Math.random() * 10));
        }else{
            result.push(hashPassword[i])
        }
    }
    console.log(result.join(''));
};
module.exports = createPasssword;

const md5 = require("js-md5");
